string MuseRecord::getTimeModification(void) {
	string output = getTimeModificationField();
	int index = 2;
	while (index >= 0 && output[index] == ' ') {
		output.resize(index);
		index--;
	}
	if (output.size() > 2) {
		if (output[0] == ' ') {
			output[0] = output[1];
			output[1] = output[2];
			output.resize(2);
		}
	}
	if (output.size() > 1) {
		if (output[0] == ' ') {
			output[0] = output[1];
			output.resize(1);
		}
	}
	if (output[0] == ' ') {
		cerr << "Error: funny error occured in time modification "
			  << "(columns 20-22): " << getLine() << endl;
		return "";
	}
	return output;
}



