//////////////////////////////
//
// Tool_pccount::getPitchClassString --
//

string Tool_pccount::getPitchClassString(int b40) {
	switch (b40%40) {
		case 0: return "C♭♭";
		case 1: return "C♭";
		case 2: return "C";
		case 3: return "C♯";
		case 4: return "C♯♯";
		// 5 is empty
		case 6: return "D♭♭";
		case 7: return "D♭";
		case 8: return "D";
		case 9: return "D♯";
		case 10: return "D♯♯";
		// 11 is empty
		case 12: return "E♭♭";
		case 13: return "E♭";
		case 14: return "E";
		case 15: return "E♯";
		case 16: return "E♯♯";
		case 17: return "F♭♭";
		case 18: return "F♭";
		case 19: return "F";
		case 20: return "F♯";
		case 21: return "F♯♯";
		// 22 is empty
		case 23: return "G♭♭";
		case 24: return "G♭";
		case 25: return "G";
		case 26: return "G♯";
		case 27: return "G♯♯";
		// 28 is empty
		case 29: return "A♭♭";
		case 30: return "A♭";
		case 31: return "A";
		case 32: return "A♯";
		case 33: return "A♯♯";
		// 34 is empty
		case 35: return "B♭♭";
		case 36: return "B♭";
		case 37: return "B";
		case 38: return "B♯";
		case 39: return "B♯♯";
	}

	return "?";
}






