int Convert::getKernBeamStartElisionLevel(const string& kerndata, int index) {
	bool foundBeamStart = false;
	int output = 0;
	int count = 0;
	int target = index + 1;
	for (int i=0; i<(int)kerndata.size(); i++) {
		char ch = kerndata[i];
		if (ch == 'L') {
			count++;
		}
		if (count == target) {
			foundBeamStart = true;
			for (int j=i-1; j>=0; j--) {
				ch = kerndata[j];
				if (ch == '&') {
					output++;
				} else {
					break;
				}
			}
			break;
		}
	}
	if (!foundBeamStart) {
		return -1;
	} else {
		return output;
	}
}



