//////////////////////////////
//
// HumPitch::setDiatonicPitchClass --
//

void HumPitch::setDiatonicPitchClass(int aDiatonicPC) {
	if (aDiatonicPC < 0) {
		m_diatonicpc = -1;
	} else if (aDiatonicPC < 7) {
		m_diatonicpc = aDiatonicPC;
	} else if (aDiatonicPC >= 'A' && aDiatonicPC <= 'G') {
		m_diatonicpc = (aDiatonicPC - 'A' + 5) % 7;
	} else if (aDiatonicPC >= 'a' && aDiatonicPC <= 'g') {
		m_diatonicpc = (aDiatonicPC - 'a' + 5) % 7;
	} else {
		m_diatonicpc = -1;
	}
}



