//////////////////////////////
//
// HumdrumToken::setStrophe -- Set the *S/ line of the strophe
//    or NULL if it is not formatted correctly.
//

void HumdrumToken::setStrophe(HTp strophe) {
	if (!strophe) {
		clearStrophe();
		return;
	}
	if (strophe->compare(0, 3, "*S/") != 0) {
		// invalid strophe marker.
		clearStrophe();
		return;
	}
	m_strophe = strophe;
}



