//
// Tool_metlev::run -- Primary interfaces to the tool.
//

bool Tool_metlev::run(HumdrumFileSet& infiles) {
	bool status = true;
	for (int i=0; i<infiles.getCount(); i++) {
		status &= run(infiles[i]);
	}
	return status;
}


