	out << "!!@PADDING: 10\n";
	out << "!!@SCALING: 1.5\n";
	out << "!!@SVGFORMAT: yes\n";
	out << "!!@TRANSPARENT: yes\n";
	out << "!!@ANTIALIAS: no\n";
	out << "!!@EMBEDPMX: yes\n";
